function renderProjetos(lista, containerId) {
  const container = document.getElementById(containerId);
  container.innerHTML = "";
  lista.forEach(projeto => {
    const card = document.createElement("div");
    card.className = "proj-card";
    card.innerHTML = `
      <a class="custom-link">
        <img src="${projeto.img}" alt="${projeto.titulo}">
        <h4>${projeto.titulo}</h4>
        <p>${projeto.desc}</p>
      </a>
    `;
    card.addEventListener("click", () => openModal(projeto));
    container.appendChild(card);
  });
}


// Modal
const modal = document.getElementById("modal");
modal.classList.add("modal");
const modalBody = document.getElementById("modal-body");
const closeBtn = document.querySelector(".close");

function openModal(projeto) {
  modalBody.innerHTML = `
    <h4 class="modal-titulo">${projeto.titulo}</h4>
    <p class="center">
      ${projeto.link ? `<a href="${projeto.link}" target="_blank">
        <img src="${projeto.img}" alt="${projeto.titulo}">
      </a>` : `<img src="${projeto.img}" alt="${projeto.titulo}">`}
    </p>
    <h5 class="modal-subtitulo">Descrição:</h5>
    <p class="modal-conteudo">${projeto.desc}</p>
    <h5 class="modal-subtitulo">Contribuições:</h5>
    <p class="modal-conteudo">${projeto.contr}</p>
    ${projeto.link ? `<a href="${projeto.link}" target="_blank" class="center">Confira mais detalhes</a>` : ""}
    <h5 class="modal-subtitulo">Hard Skills:</h5>
    <div class="technologies">
      ${projeto.hardskils.map(skill => `<span class="badge hard">${skill}</span>`).join('')}
    </div>
    <h5 class="modal-subtitulo">Soft Skills:</h5>
    <div class="technologies">
      ${projeto.softskills.map(skill => `<span class="badge soft">${skill}</span>`).join('')}
    </div>
  `;
  modal.classList.add("show");
}

closeBtn.onclick = () => modal.classList.remove("show");
window.onclick = (e) => { if (e.target === modal) modal.classList.remove("show"); };


// Inicialização
const categorias = {
  profissionais: projetosProfissionais,
  academicos: projetosAcademicos,
  pessoais: projetosPessoais
};

Object.entries(categorias).forEach(([tipo, lista]) => {
  renderProjetos(lista, `projetos${tipo.charAt(0).toUpperCase() + tipo.slice(1)}`);
});

function safeText(text) {
  return text ? text : "Informação não disponível.";
}
